import React from "react";
import { Card, Col, Row } from "react-bootstrap";
import "../stylesheet/Arrow.css"
export const ViewFlight = ({ fl }) => {
  function subtractTimes(time1, time2) {
    const date1 = new Date(`1970-01-01T${time1}Z`);
    const date2 = new Date(`1970-01-01T${time2}Z`);
    const diff = Math.abs(date1 - date2);
    const hours = Math.floor(diff / (1000 * 60 * 60))
      .toString()
      .padStart(2, "0");
    const minutes = Math.floor((diff / (1000 * 60)) % 60)
      .toString()
      .padStart(2, "0");
    const seconds = Math.floor((diff / 1000) % 60)
      .toString()
      .padStart(2, "0");
    return `${hours}:${minutes}:${seconds}`;
  }

  return (
    <div>
      <Card style={{ marginTop: "1em" }}>
        <Card.Header>{fl.flightName}</Card.Header>
        <Card.Body>
          <Row>
            <Col md={2}>{fl.departureTime}</Col>
            <Col md={8} className="arrow-col"></Col>
            <Col md={2}>{subtractTimes(fl.arrivalTime, fl.departureTime)}</Col>
          </Row>
        </Card.Body>
      </Card>
    </div>
  );
};
