import React from "react";
import { Card, Col, Row } from "react-bootstrap";
import "../stylesheet/Flight.css";
export const ViewFlight = ({ fl }) => {
  function subtractTimes(time1, time2) {
    const date1 = new Date(`1970-01-01T${time1}Z`);
    const date2 = new Date(`1970-01-01T${time2}Z`);
    const diff = Math.abs(date1 - date2);
    const hours = Math.floor(diff / (1000 * 60 * 60))
      .toString()
      .padStart(2, "0");
    const minutes = Math.floor((diff / (1000 * 60)) % 60)
      .toString()
      .padStart(2, "0");
    if (minutes === "00") {
      return `${hours} hours`;
    } else {
      return `${hours} hours ${minutes} minutes`;
    }
  }
  function convertTo12HourFormat(time) {
    let hour = parseInt(time.substring(0, 2));
    let minute = time.substring(3, 5);
    let meridiem = "AM";

    if (hour > 12) {
      hour = hour - 12;
      meridiem = "PM";
    } else if (hour === 12) {
      meridiem = "PM";
    } else if (hour === 0) {
      hour = 12;
    }

    return `${hour}:${minute} ${meridiem}`;
  }
  function convertDate(date) {
    const dateObj = new Date(date);
    const options = { month: "short", day: "numeric", year: "numeric" };
    const formattedDate = dateObj.toLocaleDateString("en-US", options);
    return formattedDate;
  }

  return (
    <div>
      <Card style={{ marginTop: "1em" }}>
        <Card.Header>
          <Row className="d-flex justify-content-evenly">
            <Col md={4} xs={4} className="flighttime">
              {convertTo12HourFormat(fl.departureTime)}
            </Col>

            <Col md={4} xs={4}>
              {subtractTimes(fl.arrivalTime, fl.departureTime)}
            </Col>
            <Col md={4} xs={4} className="flighttime">
              {convertTo12HourFormat(fl.arrivalTime)}
            </Col>
          </Row>
          <Row>
            <Col md={4} xs={4} className="flightdate">
              {convertDate(fl.departureDatetime)}
            </Col>

            <Col md={4} xs={4}></Col>
            <Col md={4} xs={4} className="flightdate">
              {convertDate(fl.arrivalDatetime)}
            </Col>
          </Row>
          <Row>
            <Col md={4} xs={4} className="flightdate">
              {fl.sourceLocation}
            </Col>

            <Col md={4} xs={4}></Col>
            <Col md={4} xs={4} className="flightdate">
              {fl.destinationLocation}
            </Col>
          </Row>
        </Card.Header>
        <Card.Body>
        
          
          
          Name {fl.flightName}<br />
          Airline Name {fl.airlineName}<br />
          Price {fl.flightPrice}<br />
          Seats Available {fl.seatCount}<br />
        </Card.Body>
        <Card.Footer>
        <div className="d-flex justify-content-evenly">
            <button className="btn btn-outline-success">Remove Flight</button>
            <button className="btn btn-outline-danger">View Booking</button>
        </div>
        </Card.Footer>
      </Card>
    </div>
  );
};
