import Button from "react-bootstrap/Button";
import Container from "react-bootstrap/Container";
import Form from "react-bootstrap/Form";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import NavDropdown from "react-bootstrap/NavDropdown";
import { Link } from "react-router-dom";
import { useRecoilState } from "recoil";
import { Jwt, User } from "../Atom/Atom";
import { useEffect } from "react";
import axios from "../Axios/Axios";

function NavBar() {
  const [user, setUser] = useRecoilState(User);
  const[jwt,setjwt]=useRecoilState(Jwt);
  useEffect(()=>
  {
      axios.post("/verifyuser",{},{headers:{
        token:jwt
      }}).then((result)=>
      {
        if(result.data.status)
        {
          setUser({status:true,name:result.data.name});
        }
        else
        {
          localStorage.removeItem("JWToken")
          setUser({status:false,name:''})
        }
      })
  },[])
  return (
    <Navbar bg="light" expand="lg">
      <Container>
        <Navbar.Brand href="#">AirBook</Navbar.Brand>
        <Navbar.Toggle aria-controls="navbarScroll" />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: "100px" }}
            navbarScroll
          >
            <Nav.Link>
              <Link to="/" className="Link">Booking</Link>{" "}
            </Nav.Link>
            <Nav.Link>
              <Link to="/mybooking" className="Link">MyBookings</Link>
            </Nav.Link>
          </Nav>
          <Nav className="d-flex">
            {user.name}
            </Nav>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default NavBar;
