import React, { useState } from 'react';

const PassengerInfo = () => {
  const [passengerCount, setPassengerCount] = useState(0);
  const [passengers, setPassengers] = useState([]);

  const handlePassengerCountChange = (e) => {
    const count = parseInt(e.target.value);
    setPassengerCount(count);
    setPassengers(Array(count).fill({ name: '', gender: '', ageGroup: '', phoneNumber: '' }));
  };

  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const newPassengers = [...passengers];
    newPassengers[index][name] = value;
    setPassengers(newPassengers);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log(passengers);
  };

  return (
    <div>
      <label htmlFor="passenger-count">Number of passengers:</label>
      <input
        type="number"
        id="passenger-count"
        value={passengerCount}
        onChange={handlePassengerCountChange}
      />

      {passengers.map((passenger, index) => (
        <div key={index}>
          <h3>Passenger {index + 1}</h3>
          <label htmlFor={`name-${index}`}>Name:</label>
          <input
            type="text"
            id={`name-${index}`}
            name="name"
            value={passenger.name}
            onChange={(e) => handleInputChange(e, index)}
          />

          <label htmlFor={`gender-${index}`}>Gender:</label>
          <input
            type="text"
            id={`gender-${index}`}
            name="gender"
            value={passenger.gender}
            onChange={(e) => handleInputChange(e, index)}
          />

          <label htmlFor={`age-group-${index}`}>Age Group:</label>
          <input
            type="text"
            id={`age-group-${index}`}
            name="ageGroup"
            value={passenger.ageGroup}
            onChange={(e) => handleInputChange(e, index)}
          />

          <label htmlFor={`phone-number-${index}`}>Phone Number:</label>
          <input
            type="text"
            id={`phone-number-${index}`}
            name="phoneNumber"
            value={passenger.phoneNumber}
            onChange={(e) => handleInputChange(e, index)}
          />
        </div>
      ))}

      <button onClick={handleSubmit}>Submit</button>
    </div>
  );
};

export default PassengerInfo;
