import React, { useState } from 'react'
import { ToastContainer, toast } from 'react-toastify';
import axios from '../Axios/Axios';
import { Col, Container, Form, Row } from 'react-bootstrap';

export const UserSignup = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phoneNumber: '',
    address: '',
    password: ''
  });

  // handle form submit
  function handleSubmit(event) {
    event.preventDefault();
    axios.post("/signup",formData).then((result)=>
    {
      if(result.data.status)
      {
        toast.success("Signed Successfully")
      }
      else
      {
        toast.info(result.data.msg)
      }
    })
  }

  // handle form field changes
  function handleChange(event) {
    setFormData({
      ...formData,
      [event.target.name]: event.target.value
    });
  }

  // render form
  return (<>
  <ToastContainer/>
  <Container>
    <Row>
      <Col md={3} sm={0}></Col>
      <Col md={6} sm={12}>
    <Form onSubmit={handleSubmit}>
      <label>
        Name:<br />
        <Form.Control type="text" name="name" value={formData.name} onChange={handleChange} />
      </label>
      <br />
      <Form.Label>
        Email:<br />
        <Form.Control type="email" name="email" value={formData.email} onChange={handleChange} />
      </Form.Label>
      <br />
      <label>
        Phone Number:<br />
        <Form.Control type="tel" name="phoneNumber" value={formData.phoneNumber} onChange={handleChange} />
      </label>
      <br />
      <label>
        Address:<br />
        <Form.Control name="address" value={formData.address} onChange={handleChange} />
      </label>
      <br />
      <label>
        Password:<br />
        <input type="password" name="password" value={formData.password} onChange={handleChange} />
      </label>
      <br />
      <button type="submit">Submit</button>
    </Form></Col><Col md={3} sm={0}></Col></Row></Container></>)
}
