import Container from "react-bootstrap/Container";
import Nav from "react-bootstrap/Nav";
import Navbar from "react-bootstrap/Navbar";
import { useRecoilState } from "recoil";
import { Admin, AdminJwt } from "../Atom/Atom";
import { Link, useNavigate } from "react-router-dom";
import { useEffect } from "react";
import axios from "../Axios/Axios";

function Menu() {
  const [admin, setAdmin] = useRecoilState(Admin);
  const[adminjwt,setAdminjwt]=useRecoilState(AdminJwt)
  const navigate=useNavigate();
  useEffect(()=>
  {
    axios.post("/verifyadmin",{},{headers:{
      token:adminjwt
    }}).then((result)=>
    {
      if(result.data.status)
      {
        setAdmin({name:result.data.name,status:true})
      }
      else
      {
        navigate("/admin/login")
      }
    }).catch((err)=>console.log(err))
  },[]);
  const handleLogout=(e)=>
  {
    e.preventDefault();
    localStorage.removeItem("AdminJWToken")
    setAdmin({status:false,name:''})
    setAdminjwt('');
  }
  return (
    <Navbar collapseOnSelect expand="lg" bg="light" variant="dark">
      <Container>
        <Navbar.Brand style={{ color: "black" }}>AirBook</Navbar.Brand>
        <Navbar.Toggle aria-controls="responsive-navbar-nav dark" />
        <Navbar.Collapse id="responsive-navbar-nav dark">
          <Nav className="me-auto">
            <Nav.Link>
              <Link to="/admin" className="Link">
                Flight
              </Link>
            </Nav.Link>
            <Nav.Link>
              <Link to="addflight" className="Link">
                AddFlight
              </Link>
            </Nav.Link>
          </Nav>
          
          <Nav.Link>{admin.name}</Nav.Link>{"  "}{"  "}
          <Nav.Link onClick={handleLogout}>Logout</Nav.Link>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
}

export default Menu;
