import React, { useEffect, useState } from "react";
import axios from "../Axios/Axios";
import { Col, Container, Form, Row } from "react-bootstrap";
import { useRecoilState } from "recoil";
import { Load } from "../Atom/Atom";
import { Flights } from "./Flights";
import Loading from "../Components/Loading";

export const Booking = () => {
  const [flight, setFlight] = useState();
  const [loading, setLoading] = useRecoilState(Load);
  const [sourceLocation, setSourceLocation] = useState([]);
  const [destinationLocation, setDestinationLocation] = useState([]);
  const [formData, setFormData] = useState({
    destinationLocation: "",
    sourceLocation: "",
    date: "yyyy-mm-dd",
  });

  useEffect(() => {
    setLoading(true);
    axios
      .post("/flight")
      .then(async (result) => {
        await setFlight(result.data.result);
        setLoading(false);
      })
      .catch((err) => console.log(err));
  }, [formData]);
  useEffect(() => {
    setLoading(true);
    axios
      .post("/location")
      .then((result) => {
        setLoading(false)
        setDestinationLocation(result.data.des);
        console.log(result.data.sor)
        setSourceLocation(result.data.sou);
        console.log(result.data.des)
      })
      .catch((err) => console.log(err));
  }, []);
  console.log(sourceLocation,destinationLocation[0])
  const handleSourceLocationChange = (e) => {
    setFormData({
      ...formData,
      sourceLocation: e.target.value,
    });
  };
  return (
    <Container>{loading?(<Loading/>):(<>
      <div className="filter-form">
        <Form>
          <Row>
            <Col md={3}></Col>
            <Col md={3}>
              <Form.Control
                as="select"
                name="sourceLocation"
                value={formData.sourceLocation}
                onChange={handleSourceLocationChange}
              >
                <option value="">Select location</option>
                {sourceLocation.map((l, index) => (
                  <option key={index} value={l.sourceLocation}>
                    {l.sourceLocation}
                  </option>
                ))}
              </Form.Control>
            </Col>
            <Col md={3}></Col>
            <Col md={3}></Col>
          </Row>
        </Form>
      </div>
      {!loading && flight ? (
        <>
          {flight.map((f, k) => (
            <Flights fl={f} />
          ))}
        </>
      ) : (
        <></>
      )}</>)}
    </Container>
  );
};
