import React, { useEffect, useState } from 'react'
import axios from '../Axios/Axios';
import { Col, Container, Form, Row } from 'react-bootstrap';
import { useRecoilState } from 'recoil';
import { Loading } from '../Atom/Atom';
import { Flights } from './Flights';

export const Booking = () => {
    const[flight,setFlight]=useState();
    const[loading,setLoading]=useRecoilState(Loading)
    const[sourceLocation,setSourceLocation]=useState([]);
    const[destinationLocation,setDestinationLocation]=useState([]);
    const[formData,setFormData]=useState({
        destinationLocation:"",
        sourceLocation:"",
        date:"yyyy-mm-dd",
    })

    useEffect(()=>
    {
        setLoading(true)
        axios.post("/flight").then(async(result)=>
        {
            await setFlight(result.data.result);
            setLoading(false);
        }).catch((err)=>console.log(err));
    },[formData])
    useEffect(()=>
    {
        axios.post("/location").then((result)=>
        {
            setDestinationLocation(result.data.sou)
            setSourceLocation(result.data.des)
        }).catch((err)=>console.log(err))
    },[]);
    const handleSourceLocationChange = (e) => {
        setFormData({
          ...formData,
          sourceLocation: e.target.value
        });
    }
  return (
    <Container >
        <div className='filter-form'>
            <Form>
                <Row>
                    <Col md={3}>
                        
                    </Col>
                    <Col md={3}>
                    <Form.Control as="select" name="sourceLocation" value={formData.sourceLocation} onChange={handleSourceLocationChange}>
              <option value="">Select location</option>
              {sourceLocation.map((location, index) => (
                <option key={index} value={location}>{location}</option>
              ))}</Form.Control>
                    </Col>
                    <Col md={3}></Col>
                    <Col md={3}></Col>
                </Row>
            </Form>
        </div>
        {!loading&&flight?(<>{flight.map((f,k)=>(<Flights fl={f}/>))}</>):(<>
        </>)}
        
    </Container>
  )
}
