import React, { useEffect, useState } from "react";
import { useRecoilValue } from "recoil";
import { AdminFlightId, Jwt } from "../Atom/Atom";
import axios from "../Axios/Axios";
import { TicketDetails } from "../Pages/TicketDetails";
import { Card } from "react-bootstrap";

export const FlightBooking = () => {
  const [booking, setBooking] = useState([]);
  const fl = useRecoilValue(AdminFlightId);
  useEffect(() => {
    axios
      .post("/admin/flightbooking", { id: fl.flightId })
      .then((result) =>{ setBooking(result.data.result)
        console.log(result)
    })
      .catch((err) => console.log(err));
  }, []);
  return (
    <div>
      <Card style={{ marginTop: "1em" }}>
        <Card.Header>
          <div className="d-flex justify-content-between">
            <div className="flighttime">
              {convertTo12HourFormat(fl.departureTime)}
            </div>

            <div></div>
            <div className="flighttime">
              {convertTo12HourFormat(fl.arrivalTime)}
            </div>
          </div>
          <div className="d-flex justify-content-between">
            <div md={4} xs={4} className="flightdate">
              {convertDate(fl.departureDatetime)}
            </div>

            <div></div>
            <div className="flightdate">{convertDate(fl.arrivalDatetime)}</div>
          </div>
          <div className="d-flex justify-content-between">
            <div md={4} xs={4} className="flightdate">
              {fl.sourceLocation}
            </div>

            <div className="flightdate">{subtractTimes(fl.arrivalTime, fl.departureTime)}</div>
            <div className="flightdate">{fl.destinationLocation}</div>
          </div>
        </Card.Header>
        <Card.Body>
          <p >Name : <span>{fl.flightName}</span></p>
          <p>Airline Name : <span>{fl.airlineName}</span></p>
          <p>Price : <span>{fl.flightPrice}</span></p>
          <p>Seats Available : <span>{fl.seatCount}</span></p>
        </Card.Body>
        <Card.Footer>
          <div className="d-flex justify-content-around">
            {fl.status===1?(<button className="btn btn-outline-success" onClick={removeFlight}>Remove Flight</button>):(<button className="btn btn-success">Removed Flight</button>)}
            <button className="btn btn-outline-danger" onClick={handlebooking}>View Booking</button>
          </div>
        </Card.Footer>
      </Card>
    </div>
  );
};
