import React, { useState } from "react";
import { Form } from "react-bootstrap";

const PassengerInfo = () => {
  const [passengerCount, setPassengerCount] = useState(1);
  const [passengers, setPassengers] = useState([]);
  const [index, setIndex] = useState(1);
  const [pcount, setPCount] = useState(true);
  const handlePassengerCountChange = (e) => {
    const c = parseInt(e.target.value);
    setIndex(1)
    if (c > 9) {
      setPassengerCount(9);
      setPassengers(
        Array(9).fill({
          name: "",
          gender: "",
          ageGroup: "",
          phoneNumber: "",
        })
      );
    } else if (c <= 0) {
      setPassengerCount(1);
      setPassengers(
        Array(0).fill({
          name: "",
          gender: "",
          ageGroup: "",
          phoneNumber: "",
        })
      );
    } else {
      setPassengerCount(c);
      setPassengers(
        Array(c || 1).fill({
          name: "",
          gender: "",
          ageGroup: "",
          phoneNumber: "",
        })
      );
    }
    
    console.log(passengers);
  };
  const handleNext=(e)=>
  {
    e.preventDefault();
    setIndex(prev=>prev+1);
  }
  const handleInputChange = (e, index) => {
    const { name, value } = e.target;
    const newPassengers = [...passengers];
    newPassengers[index - 1] = {
      ...newPassengers[index - 1],
      [name]: value
    };
    setPassengers(newPassengers);
  };
  console.log(passengers)
  

  return (<div className="d-flex justify-content-center">
  <div className="col-md-6">
    <>
      {pcount ? (
        <>
        <label htmlFor={`no`}>Enter no of Passengers:</label>
          <Form.Control
            type="number"
            id="no"
            value={passengerCount}
            onChange={handlePassengerCountChange}
          />
          <button className="btn btn-outline-primary" onClick={(e) => setPCount(!pcount)}>Next</button>
        </>
      ) : (
        <>
        <label htmlFor={`no1`}>Number of Passengers:</label>
          <Form.Control
            type="number"
            id="no1"
            onChange={handlePassengerCountChange}
            disabled
          /><br />
          <button className="btn btn-outline-primary" onClick={(e) => setPCount(!pcount)}>Edit</button>
        </>
      )}
      <br />
      {!pcount ? (
        <>
          passenger {index}<br />
          <form >
            <label htmlFor={`name-${index}`}>Name:</label>
            <input
              type="text"
              id={`name-${index}`}
              name="name"
              required
              value={passengers[index - 1]?.name}
              onChange={(e) => handleInputChange(e, index)}
            /><br /><br />
            <label htmlFor={`gender-${index}`}>Gender:</label>
            <select
              id={`gender-${index}`}
              name="gender"
              value={passengers[index - 1]?.gender}
              onChange={(e) => handleInputChange(e, index)}
            >
              <option value="">Select Gender</option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select><br /><br />
            <label htmlFor={`age-group-${index}`}>Age Group:</label>
            <select
              id={`age-group-${index}`}
              name="ageGroup"
              value={passengers[index - 1]?.ageGroup}
              onChange={(e) => handleInputChange(e, index)}
            >
              <option value="">Select Age Group</option>
              <option value="child">Child (below 18)</option>
              <option value="adult">Adult (18-60)</option>
              <option value="senior">Senior (above 60)</option>
            </select><br /><br />
            <label htmlFor={`phone-number-${index}`}>Phone Number:</label>
            <input
              type="text"
              id={`phone-number-${index}`}
              name="phoneNumber"
              required
              value={passengers[index - 1]?.phoneNumber}
              onChange={(e) => handleInputChange(e, index)}
            /><br /><br />
            {index>1?<button className="btn btn-outline-primary" onClick={e=>setIndex(index-1)}>Back</button>:(<></>)}
            {index<passengerCount?<button type='submit' onClick={(e)=>setIndex(prev=>prev+1)} className="btn btn-outline-primary" disabled={!passengers[index-1]?.name || !passengers[index-1]?.gender || !passengers[index-1]?.ageGroup || !passengers[index-1]?.phoneNumber}>Next</button>:(<><button className="btn btn-outline-success" type='submit' disabled={!passengers[index-1]?.name || !passengers[index-1]?.gender || !passengers[index-1]?.ageGroup || !passengers[index-1]?.phoneNumber}>Submit</button></>)}
          </form>
        </>
      ) : (
        <></>
      )}
    </>
  </div>
</div>

)
};

export default PassengerInfo;
